mapper.update:
update dish
name = #{name}, dish.category_id = #{categoryId}, dish.price = #{price}, dish.image = #{image}, dish.description = #{description}, dish.status = #{status}, dish.create_user = #{createUser}, dish.create_time = #{createTime}, dish.update_time = #{updateTime}, dish.update_user = #{updateUser},
where id = #{id}

mapper.select:
select d.*, c.name from dish d left outer join category c on d.category_id = c.id


        d.name like concat('%', #{name}, '%')


        d.category_id = #{categoryId}


        d.status = #{status}


order by d.create_time desc
